    name:'ETHtt',
    deposit:{
      amount: 1000,
      hasApproval: true
    },
    borrow:{
      amount: 100,
      hasApproval: false
    },
    withdraw:{

    },
  }
}



describe('ETH SPEC',  ()=>{
